import { Component, OnInit } from '@angular/core';
import { select, Store } from '@ngrx/store';
import { throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { setAPIStatus } from 'src/app/shared/store/app.action';
import { selectAppState } from 'src/app/shared/store/app.selector';
import { Appstate } from 'src/app/shared/store/appstate';
import { Inventory } from '../store/inventory';
import {
  invokeConsumePartAPI,
  invokeInventoryAPI,
  invokeReceivePartAPI,
} from '../store/inventory.action';
import { selectInventory, selectPartById } from '../store/inventory.selector';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent implements OnInit {
  parts$ = this.store.pipe(select(selectInventory));
  numOfParts: number;
  display: boolean = false;
  inStockQuantity: number;
  isEditing: boolean = false;
  enableEditIndex = null;
  consume: boolean = false;
  receive: boolean = false;

  constructor(
    private store: Store,
    private appStore: Store<Appstate>) {}

  partForm: Inventory = {
    id: 0,
    cost: 0,
    partNumber: '',
    description: '',
    name: '',
    notes: '',
    inStock: 20,
    image: {},
    isActive: true,
  };

  ngOnInit(): void {
    this.store.dispatch(invokeInventoryAPI());

    this.parts$.subscribe((data) => {
      this.numOfParts = data.reduce(
        (previousValue, currentValue) => previousValue + currentValue.inStock,
        0
      );
      console.log(this.numOfParts);
    });
  }




  switchEditMode(i) {
    this.isEditing = true;
    this.enableEditIndex = i;
  }


  receive1(id, index) {
    this.receive = true;
    this.switchEditMode(index);
  }



  consume1(id, index) {
    this.consume = true;
    this.switchEditMode(index);
  }

  updateInStock(id){
    let fetchdata$ = this.store.pipe(select(selectPartById(id)));
    fetchdata$.subscribe((data) => {
      if (data) {
        this.partForm = {... data};
      }
    })
    if(this.receive){
      this.store.dispatch(
        invokeReceivePartAPI({quantity: this.inStockQuantity, id: id})
      );
    } else if (this.consume){
      this.store.dispatch(
        invokeConsumePartAPI({quantity: this.inStockQuantity, id: id})
      );
    }
    let apiStatus$ = this.appStore.pipe(select(selectAppState));
    apiStatus$.subscribe((appState) => {
      if(appState.apiStatus == 'success') {
        this.appStore.dispatch(
          setAPIStatus({ apiStatus: {apiResponseMessage:'', apiStatus:''}})
        );
      }
    }),
    catchError((err) => {
      return throwError(err);
    })
    this.inStockQuantity = null;
    this.enableEditIndex = null;
  }

  // consumePart(id){

  //   let apiStatus$ = this.appStore.pipe(select(selectAppState));
  //   apiStatus$.subscribe((appState) => {
  //     if(appState.apiStatus == 'success') {
  //       this.appStore.dispatch(
  //         setAPIStatus({ apiStatus: {apiResponseMessage:'', apiStatus:''}})
  //       );
  //     }
  //   })
  //   this.inStockQuantity = null;
  //   this.enableEditIndex = null;
  // }
}
<section class="dashboard">
  <section>
    <div class="container">
      <div class="row">
        <div class="col">
          <div class="header">
            <div>
              <nav class="navbar navbar-light bg-light">
                <form class="form-inline">
                  <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search">
                  <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </form>
              </nav>
            </div>
            <div>
              <h1>{{numOfParts}}</h1>
            </div>
            <div>
              <a type="button" routerLink="/add" class="btn btn-dark">Add A New Part</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>

  <section>
    <div class="container">
      <div class="row">
        <div class="col">
          <div>
            <table class="table table-hover">
              <thead class="thead-dark">
                <tr>
                  <th scope="col" class="text-center">#</th>
                  <th scope="col" class="text-left">Name</th>
                  <th scope="col" class="text-center">Number</th>
                  <th scope="col" class="text-center">Cost</th>
                  <th scope="col" class="text-center">In-stock</th>
                  <th scope="col" class="text-center"></th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let part of parts$| async ; index as i">
                  <td scope="row" class="text-center">{{i+1}}</td>
                  <td class="text-left">{{part.name}}</td>
                  <td class="text-center">{{part?.partNumber}}</td>
                  <td class="text-center">{{part?.cost}}</td>
                  <td class="text-center">
                    {{part?.inStock}}

                    <button (click)="receive1(part.id,i);">+</button>
                    <button (click)="consume1(part.id,i);">--</button>
                    <div *ngIf="enableEditIndex == i">
                      <input type="number" [(ngModel)]="inStockQuantity" class="form-control" id="txtinStock" />
                      <button (click)="updateInStock(part.id)">save</button>
                    </div>
                  </td>
                  <td> <a [routerLink]="['/edit', part.id]" class="btn btn-dark">Edit</a></td>
                </tr>
              </tbody>
              <div>

              </div>
            </table>
          </div>
        </div>
      </div>
    </div>

  </section>
</section>
