import { Component, OnInit } from '@angular/core';
import { select, Store } from '@ngrx/store';
import { setAPIStatus } from 'src/app/shared/store/app.action';
import { selectAppState } from 'src/app/shared/store/app.selector';
import { Appstate } from 'src/app/shared/store/appstate';
import { Inventory } from '../store/inventory';
import {
  invokeInventoryAPI,
  invokeReceivePartAPI,
} from '../store/inventory.action';
import { selectInventory, selectPartById } from '../store/inventory.selector';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent implements OnInit {
  parts$ = this.store.pipe(select(selectInventory));
  numOfParts: number;
  display: boolean = false;

  constructor(
    private store: Store,
    private appStore: Store<Appstate>) {}

  partForm: Inventory = {
    id: 0,
    cost: 0,
    partNumber: '',
    description: '',
    name: '',
    notes: '',
    inStock: 20,
    image: {},
    isActive: true,
  };

  ngOnInit(): void {
    this.store.dispatch(invokeInventoryAPI());

    this.parts$.subscribe((data) => {
      this.numOfParts = data.reduce(
        (previousValue, currentValue) => previousValue + currentValue.inStock,
        0
      );
      console.log(this.numOfParts);
    });
  }

  receive(id) {
    console.log('receive', id);
    this.display = true;
    let fetchdata$ = this.store.pipe(select(selectPartById(id)));
    fetchdata$.subscribe((data) => {
      if (data) {
        this.partForm = {... data};
      }
    })
    // this.display = true;
  }

  consume(id) {
    console.log('consume', id);
    this.display = false;
  }

  someMethod(id){
    this.store.dispatch(
      invokeReceivePartAPI({quantity: 10, id: id})
    );
    let apiStatus$ = this.appStore.pipe(select(selectAppState));
    apiStatus$.subscribe((appState) => {
      if(appState.apiStatus == 'success') {
        this.appStore.dispatch(
          setAPIStatus({ apiStatus: {apiResponseMessage:'', apiStatus:''}})
        );
      }
    })
    this.display= false;
  }
}
<section class="container">
  <div class="row">
    <div class="col">
      <div>
        <table class="table table-hover">
          <thead class="thead-dark">
            <tr>
              <th scope="col" class="text-center">#</th>
              <th scope="col" class="text-left">Name</th>
              <th scope="col" class="text-center">Number</th>
              <th scope="col" class="text-center">Cost</th>
              <th scope="col" class="text-center">In-stock</th>
              <th scope="col" class="text-center"></th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let part of parts$| async ; index as i">
              <td scope="row" class="text-center">{{i+1}} {{i}}</td>
              <td class="text-left">{{part.name}}</td>
              <td class="text-center">{{part?.partNumber}}</td>
              <td class="text-center">{{part?.cost}}</td>
              <td class="text-center">
                  {{part?.inStock}}

                <button (click)="receive(part.id);">+</button>
                <button (click)="consume(part.id)">--</button>
                <div *ngIf="display">
                  <input
                  type="number"
                  [(ngModel)]="partForm.inStock"
                  class="form-control"
                  id="txtinStock"
                />
                  <button (click)="someMethod()">save</button>
                </div>
            </td>
              <td> <a [routerLink]="['/edit', part.id]" class="btn btn-dark">Edit</a></td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
</section>
