import { Component, OnInit } from '@angular/core';
import { select, Store } from '@ngrx/store';
import { invokeInventoryAPI } from '../store/inventory.action';
import { selectInventory } from '../store/inventory.selector';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  parts$ = this.store.pipe(select(selectInventory));
  numOfParts: number;

  constructor(private store: Store) {}

  ngOnInit(): void {
    this.store.dispatch(invokeInventoryAPI());
    console.log(this.parts$);
    this.parts$.subscribe((data) => {
      this.numOfParts = data.length;
      console.log(this.numOfParts)
    })
  }

}
<section class="container">
  <div class="row row-cols-1 row-cols-md-3 g-4">
    <div class="col" *ngFor="let part of parts$ | async">








      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{part.name}}</h5>
          <a [routerLink]="['/edit', part.id]" class="btn btn-dark">Edit part</a>
        </div>
        <ul class="list-group list-group-flush">
          <li class="list-group-item"><strong>PartNumber:</strong> {{part?.partNumber}} </li>
          <li class="list-group-item"><strong>Cost:</strong> {{part?.cost}} </li>
          <li class="list-group-item"><strong>In-stock:</strong> {{part?.inStock}}
        </li>
          <li class="list-group-item"><strong>Description:</strong> {{part?.description}} </li>
        </ul>
      </div>
    </div>
  </div>
</section>


<table class="table table-hover">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Name</th>
      <th scope="col">Cost</th>
      <th scope="col">In-stock</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let part of parts$ | async">
      <th scope="row">1</th>
      <td>{{part?.partNumber}}</td>
      <td>{{part?.cost}}</td>
      <td>{{part?.inStock}}</td>
    </tr>
  </tbody>
</table>
